export default function AnimationForFilter({title, list}){
    var list = [...list]

    function handdleClick(){
        const filter = document.querySelectorAll(".panel-heading")
        filter.forEach((v, i) => {
            v.addEventListener("click", (e) => {
                e.stopPropagation()
                if(v.classList.contains('open')){
                    v.classList.remove("open")
                } else {
                    v.classList.add("open")
                } 
            })
        })
    }
    return (
        <div className="panel panel-default">
            <div className="panel-heading" data-toggle="collapse" data-parent="#accordion" href="#collapseOne" onClick={handdleClick}>
                <strong>{title}</strong><ion-icon name="add-outline" className="panel-icon"></ion-icon> 
            </div>
            <div id="collapseOne" className="list-group panel-collapse collapse in">
            <div className="list-group" >
                {list.map((v, i) => 
                    <a href="#" className="list-group-item" key={i}>{v}</a>
                )}
            </div>
            </div>
        </div>
    )
}
