        return (
            <div className={"cart--item "+this.state.id}>
                <img src={this.state.img} alt="" />
                <div className="cart--info-item">
                    <h3>{this.state.name}</h3>
                    <strong>${this.state.price}</strong>
                    <div className="number">
                    <ion-icon name="remove-outline" onClick={() => this.handdleClick('')}></ion-icon><span>{this.state.amount}</span><ion-icon name="add-outline" onClick={() => this.handdleCick('tai')}></ion-icon> 
                    </div>
                </div>
