import { initializeApp } from "firebase/app" 
import { getAnalytics } from "firebase/analytics"
import { getDatabase, ref, set, child, get } from "firebase/database"
import React from 'react'
import ReactDOM from 'react-dom'
import Item from "../components/item";
import { items, setItems } from "../utils/product-items"
// TODO: Add SDKs for Firebase products that you want to use 
// https://firebase.google.com/docs/web/setup#available-libraries 
// Your web app's Firebase configuration 
// For Firebase JS SDK v7.20.0 and later, measurementId is optional 
function init(){
    const firebaseConfig = { apiKey: "AIzaSyA_XBXsCvBzMTi8nWgWyCmaUZ2dmDaqDRk", authDomain: "adaleneclone-c61da.firebaseapp.com", databaseURL: "https://adaleneclone-c61da-default-rtdb.firebaseio.com", projectId: "adaleneclone-c61da", storageBucket: "adaleneclone-c61da.appspot.com", messagingSenderId: "996738155842", appId: "1:996738155842:web:e03c3a44161d0d2a93b4db", measurementId: "G-NKJ8KWJ50Y" };
    // Initialize Firebase 
    const app = initializeApp(firebaseConfig); 
    const analytics = getAnalytics(app); 
}
init()
function getData(url){
    get(child(ref(getDatabase()), url))
    .then((data) => {
        setItems(Object.entries(data.val()).map((v, i) => {
            if(v[1].sell > 10) {
                var data = {
                    id: v[0],
                    ...v[1]
                }
                return (
                    <Item
                        {...data}
                    />
                )
            }
        }))
        ReactDOM.render(items, document.querySelector('.carousel--items'))
        return 0;
    })
    .catch((error) => {
        console.log(error)
    })
}

function getFullData(url, parent){
    get(child(ref(getDatabase()), url))
    .then((data) => {
        setItems(Object.entries(data.val()).map((v, i) => {
                var data = {
                    id: v[0],
                    ...v[1]
                }
                return (
                    <Item
                        {...data}
                    />
                )
        }))
        ReactDOM.render(items, document.querySelector(`.${parent}`))
        return 0;
    })
    .catch((error) => {
        console.log(error)
    })
}

function getDataByFilter(category, value, url="products", parent="products"){
    if (category.match('Collection')){
        get(child(ref(getDatabase()), url))
        .then((data) => {
            setItems(Object.entries(data.val()).map((v, i) => {
                if(v[1].category === value || value === 'all'){
                    var data = {
                        id: v[0],
                        ...v[1]
                    }
                    return (
                        <Item {...data} />
                    )
                }
            }))
            if(items.filter(x => x !== undefined).length === 0){
                items.unshift(
                    <p style={{'text-align':'center', 'font-size': '2em', 'font-family':'Poppins'}}>No Results.</p>
                )
            }
            ReactDOM.render(items, document.querySelector(`.${parent}`))
            return 0;
        })
        .catch((error) => {
            console.log(error)
        })
    } else if(category.match('Price')){
        get(child(ref(getDatabase()), url))
        .then((data) => {
            setItems(Object.entries(data.val()).map((v, i) => {
                if((parseFloat(v[1].price) >= parseFloat(value.split(',')[0]) && parseFloat(v[1].price) <= parseFloat(value.split(',')[1]))|| value === 'all'){
                    var data = {
                        id: v[0],
                        ...v[1]
                    }
                    return (
                        <Item {...data} />
                    )
                }
            }))
            if(items.filter(x => x !== undefined).length === 0){
                items.unshift(
                    <p style={{'text-align':'center', 'font-size': '2em', 'font-family':'Poppins'}}>No Results.</p>
                )
            }
            ReactDOM.render(items, document.querySelector(`.${parent}`))
            return 0;
        })
        .catch((error) => {
            console.log(error)
        })
    }
}
function getDataByFilter2(category, value, url="products", parent="products"){
    let items = []
    if (category.match('Collection')){
        get(child(ref(getDatabase()), url))
        .then((data) => {
            setItems(Object.entries(data.val()).map((v, i) => {
                if(v[1].category === value || value === 'all'){
                    var data = {
                        id: v[0],
                        ...v[1]
                    }
                    return (
                        <Item {...data} />
                    )
                }
            }))
            if(items.filter(x => x !== undefined).length === 0){
                items.unshift(
                    <p style={{'text-align':'center', 'font-size': '2em', 'font-family':'Poppins'}}>No Results.</p>
                )
            }
            return items;
        })
        .catch((error) => {
            return []
        })
    } else if(category.match('Price')){
        get(child(ref(getDatabase()), url))
        .then((data) => {
            setItems(Object.entries(data.val()).map((v, i) => {
                if((parseFloat(v[1].price) >= parseFloat(value.split(',')[0]) && parseFloat(v[1].price) <= parseFloat(value.split(',')[1]))|| value === 'all'){
                    var data = {
                        id: v[0],
                        ...v[1]
                    }
                    return (
                        <Item {...data} />
                    )
                }
            }))
            if(items.filter(x => x !== undefined).length === 0){
                items.unshift(
                    <p style={{'text-align':'center', 'font-size': '2em', 'font-family':'Poppins'}}>No Results.</p>
                )
            }
            return items
        })
        .catch((error) => {
            return []
        })
    }
    return items;
}


function pagination(pageNumber){
    console.log(pageNumber)
    console.log(items)
    setItems(items.filter((v, i) => {
        console.log(i >= (parseInt(pageNumber) * 2 - 2) && i < (parseInt(pageNumber) * 2 ))
        return i >= (parseInt(pageNumber) * 2 - 2) && i < (parseInt(pageNumber) * 2 )
    }))
}
export { getData, getFullData, getDataByFilter, getDataByFilter2, pagination }
