import React from 'react'
import { cartItems } from '../utils/cart-items'
import sum from '../utils/sum'

export default class CartItem extends React.Component{

    constructor({id, img, name, price, amount}){
        this.state = {
            id,
            img,
            name,
            price,
            amount
        }
        super(this.state)
    }

    handdleClick(is){
        var n =  document.querySelector(".cart--item."+this.state.id+" .number>span")
        if(is){
            n.textContent = parseInt(n.textContent) + 1
            for(var i = 0; i < cartItems.length; i++){
                if(cartItems[i].state.id === this.state.id){
                    cartItems[i].setState({"amount" : n.textContent})
                    break
                }
            }
        } else{
            if(parseInt(n.textContent) >= 2){
                n.textContent = parseInt(n.textContent) - 1 
                for(var i = 0; i < cartItems.length; i++){
                    if(cartItems[i].state.id === this.state.id){
                        cartItems[i].props.setState({"amount" : n.textContent})
                        break
                    }
                }
            }
        } 
        document.querySelector("#total").textContent = sum()
    }

    handdleRemove(){
        var n =  document.querySelector(".cart--item."+this.state.id)
        var parent = n.parentElement
        parent.removeChild(n)
        document.querySelector("#total").textContent = sum()
    }

    render(){
        return (
            <div className={"cart--item "+this.state.id}>
                <img src={img} alt="" />
                <div className="cart--info-item">
                    <h3>{name}</h3>
                    <strong>${price}</strong>
                    <div className="number">
                    <ion-icon name="remove-outline" onClick={() => handdleClick('')}></ion-icon><span>{amount}</span><ion-icon name="add-outline" onClick={() => handdleClick('tai')}></ion-icon> 
                    </div>
                </div>
                <div className="cart--item-close" onClick={handdleRemove}>
                    <ion-icon name="close-outline"></ion-icon>
                </div>
            </div>
        )
    }

}
